import Link from 'next/link'
import { Select,Checkbox } from 'antd';
const CheckboxGroup = Checkbox.Group;

const plainOptions = ['Apple', 'Pear', 'Orange'];

const { Option } = Select;
class Sidebar extends React.Component{
    render(){
        function onChange(checkedValues) {
            console.log('checked = ', checkedValues);
          }
          
          const plainOptions = ['Apple'];
          
          const optionsWithDisabled = [
            { label: 'Apple', value: 'Apple' }
          ];
        return(
            <div className="sidebar">
                <h1>Area Manager View</h1>
                <div className="barns">
                    <p>Barn Filter</p>
                    <Select
                        showSearch
                        placeholder="All Bams"
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                        option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        <Option value="jack">Jack</Option>
                        <Option value="lucy">Lucy</Option>
                        <Option value="tom">Tom</Option>
                    </Select>
                </div>
                <div className="checkbox">
                        <div className="allBams">
                            <Checkbox.Group
                            options={plainOptions} defaultValue={['Apple']} onChange={onChange}
                            /> 
                        </div>
                        <div className="bam1">
                            <Checkbox 
                            options={plainOptions} defaultValue={['Pear']} onChange={onChange}
                            /> 
                        </div>
                        <div className="bam2">
                            <Checkbox 
                            options={plainOptions} defaultValue={['Orange']} onChange={onChange}
                            /> 
                        </div>
                </div>
            </div>
            // <Layout>
            //     <Link href="/"><a>Trang chủ</a></Link>
            //     <User />
            // </Layout>
        );
    }
}
export default Sidebar;
